function setup(){rectMode(CENTER),colorMode(HSB),blendMode(MULTIPLY),noStroke();for(var i=0;i<circlesAmount;i++)randomDist[i]=random(120,1280),circles.push(new circle(randomDist[i]))}function draw(){createCanvas(windowWidth,windowHeight),background(255),translate(windowWidth/2,windowHeight/2);for(var i=0;i<circlesAmount;i++)circles[i].show(),circles[i].speedcheck(),circles[i].update()}function windowResized(){resizeCanvas(windowWidth,windowHeight)}function mousePressed(){if(mouseX>0&&mouseX<100&&mouseY>0&&mouseY<100){var i=fullscreen();fullscreen(!i)}}function circle(i){this.i=floor(randomGaussian(0,360)),this.speed=randomGaussian(.1,.8),this.offset=i,this.minSize=24,this.maxSize=64,this.size=randomGaussian(this.minSize,this.maxSize),this.incrementer=1,this.direction=1,this.j,this.nx,this.ny,this.colorV=floor(random(360)),this.show=function(){blendMode(MULTIPLY),fill(this.colorV,120,255),ellipse(this.nx,this.ny,this.size,this.size)},this.update=function(){this.i>360&&(this.i=this.i%360),this.j=this.i*Math.PI/180,this.nx=Math.cos(this.j)*this.offset,this.ny=Math.sin(this.j)*this.offset,this.i+=this.speed,this.size+=this.incrementer*this.direction,this.incrementer+=1e-4,console.log(this.size)},this.speedcheck=function(){this.size>2*this.maxSize?(this.direction=-1,this.incrementer=1):this.size<=this.minSize&&(this.direction=1)}}var circles=[],randomDist=[],randomSize=[],circlesAmount=480;